Bash(echo "

/* Enhanced User Activity Card Styles */
.user-activity-card {
  grid-column: span 2;
}

.user-activity-enhanced {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 1.5rem;
  margin-top: 1rem;
}

.activity-item-enhanced {
  display: flex;
  align-items: flex-start;
  gap: 1rem;
  padding: 1.25rem;
  background: linear-gradient(135deg, #f8f9fa 0%, #ffffff 100%);
  border-radius: 12px;
  border: 1px solid rgba(139, 90, 43, 0.1);
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
}

.activity-item-enhanced::before {
  content: '''';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 3px;
  background: linear-gradient(90deg, #8B5A2B, #D4A76A);
  opacity: 0;
  transition: opacity 0.3s ease;
}

.activity-item-enhanced:hover {
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(139, 90, 43, 0.15);
  border-color: rgba(139, 90, 43, 0.2);
}

.activity-item-enhanced:hover::before {
  opacity: 1;
}

.activity-icon-wrapper {
  width: 56px;
  height: 56px;
  border-radius: 12px;
  background: linear-gradient(135deg, rgba(139, 90, 43, 0.1), rgba(212, 167, 106, 0.1));
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  transition: all 0.3s ease;
}

.activity-item-enhanced:hover .activity-icon-wrapper {
  transform: scale(1.1);
  background: linear-gradient(135deg, rgba(139, 90, 43, 0.15), rgba(212, 167, 106, 0.15));
}

.activity-content {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.activity-value-large {
  font-size: 2rem;
  font-weight: 700;
  color: #8B5A2B;
  line-height: 1;
}

.activity-label {
  font-size: 0.875rem;
  color: #666;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.activity-progress-bar {
  width: 100%;
  height: 6px;
  background: rgba(139, 90, 43, 0.1);
  border-radius: 3px;
  overflow: hidden;
  margin-top: 0.25rem;
}

.activity-progress-fill {
  height: 100%;
  background: linear-gradient(90deg, #8B5A2B, #D4A76A);
  border-radius: 3px;
  transition: width 0.5s ease;
}

.activity-progress-fill.completed {
  background: linear-gradient(90deg, #4CAF50, #8BC34A);
}

.activity-progress-fill.achievement {
  background: linear-gradient(90deg, #FFD700, #FFA500);
}

.activity-progress-fill.completed-courses {
  background: linear-gradient(90deg, #FF9800, #FF5722);
}

/* Responsive layout for smaller screens */
@media (max-width: 768px) {
  .user-activity-card {
    grid-column: span 1;
  }

  .user-activity-enhanced {
    grid-template-columns: 1fr;
    gap: 1rem;
  }

  .activity-item-enhanced {
    padding: 1rem;
  }

  .activity-icon-wrapper {
    width: 48px;
    height: 48px;
  }

  .activity-value-large {
    font-size: 1.75rem;
  }
}")
